)A
)temp

select * from ( select sum(num1) sum1,sum(num2) sum2,sum(num3) sum3,sum(num4) sum4,sum(num5) sum5,sum(num6) sum6,sum(num7) sum7,sum(num8) sum8,sum(num9) sum9,sum(num10) sum10 from (select case when round(sum(fworktime)/60/60)>=#{minnum} and round(sum(fworktime)/60/60)<=#{minnum}+#{avgnum} then 1 else 0 end as num1, case when round(sum(fworktime)/60/60)>#{minnum}+#{avgnum} and round(sum(fworktime)/60/60)<=#{minnum}+#{avgnum}*2 then 1 else 0 end as num2, case when round(sum(fworktime)/60/60)>#{minnum}+#{avgnum}*2 and round(sum(fworktime)/60/60)<=#{minnum}+#{avgnum}*3 then 1 else 0 end as num3, case when round(sum(fworktime)/60/60)>#{minnum}+#{avgnum}*3 and round(sum(fworktime)/60/60)<=#{minnum}+#{avgnum}*4 then 1 else 0 end as num4, case when round(sum(fworktime)/60/60)>#{minnum}+#{avgnum}*4 and round(sum(fworktime)/60/60)<=#{minnum}+#{avgnum}*5 then 1 else 0 end as num5, case when round(sum(fworktime)/60/60)>#{minnum}+#{avgnum}*5 and round(sum(fworktime)/60/60)<=#{minnum}+#{avgnum}*6 then 1 else 0 end as num6, case when round(sum(fworktime)/60/60)>#{minnum}+#{avgnum}*6 and round(sum(fworktime)/60/60)<=#{minnum}+#{avgnum}*7 then 1 else 0 end as num7, case when round(sum(fworktime)/60/60)>#{minnum}+#{avgnum}*7 and round(sum(fworktime)/60/60)<=#{minnum}+#{avgnum}*8 then 1 else 0 end as num8, case when round(sum(fworktime)/60/60)>#{minnum}+#{avgnum}*8 and round(sum(fworktime)/60/60)<=#{minnum}+#{avgnum}*9 then 1 else 0 end as num9, case when round(sum(fworktime)/60/60)>#{minnum}+#{avgnum}*9 and round(sum(fworktime)/60/60)<=#{minnum}+10+#{avgnum}*10 then 1 else 0 end as num10 FROM tb_work l left join tb_welder w on l.fwelder_id = w.fwelder_no inner join tb_welded_junction j on l.fjunction_id = j.fwelded_junction_no inner join tb_insframework i on i.fid = l.fitemid inner join tb_insframework ins on ins.fid = i.fparent where 1=1
AND (i.fid = #{parent} or i.fparent=#{parent} or ins.fparent=#{parent})
and fstarttime >= #{dto.dtoTime1}
and fendtime <= #{dto.dtoTime2}
group by w.fname ORDER by round(sum(fworktime)/60/60))A)temp

select sum(time) from ( select sum(fworktime) time from tb_work l INNER JOIN tb_insframework i on i.fid = l.fitemid INNER JOIN tb_insframework ins on ins.fid = i.fparent where fstarttime >= #{time} and fendtime <= date_sub(date_sub(#{time},interval -1 day),interval 0 minute)
and (i.fid=#{parent} or ins.fid=#{parent} or ins.fparent=#{parent})
and fmachine_id = #{mid}
UNION ALL select sum(fstandbytime) time from tb_standby l INNER JOIN tb_insframework i on i.fid = l.fitemid INNER JOIN tb_insframework ins on ins.fid = i.fparent where fstarttime >= #{time} and fendtime <= date_sub(date_sub(#{time},interval -1 day),interval 0 minute)
and (i.fid=#{parent} or ins.fid=#{parent} or ins.fparent=#{parent})
and fmachine_id = #{mid}
)temp

select fwelded_junction_no fname,i.fname iname ,fmax_electricity,fmin_electricity,fmax_valtage,fmin_valtage,fwall_thickness,fnextwall_thickness,Fmaterial,Fnext_material,fexternal_diameter,fnextExternal_diameter from tb_live_data l INNER join tb_welder w on l.fwelder_id = w.fwelder_no INNER join tb_welded_junction j on l.fjunction_id = j.fwelded_junction_no INNER JOIN tb_insframework i ON j.fitemId = i.fid where l.fwelder_id=#{welder}
and fweldtime >=#{dto.dtoTime1} and fweldtime <= date_sub(date_sub(#{dto.dtoTime1},interval -1 day),interval 0 minute)
group by fjunction_id

SELECT m.fequipment_no fname,COUNT(l.fid) AS hous FROM tb_live_data l
INNER join tb_welding_machine m on l.fmachine_id=m.fid WHERE 1=1 


		AND l.FWeldTime >= #{dto.dtoTime1}


		AND l.FWeldTime <= #{dto.dtoTime2}


AND l.fstatus='0' 
GROUP BY m.fequipment_no
UNION
SELECT m.fequipment_no fname,COUNT(l.fid) AS hous FROM tb_live_data l 
INNER join tb_welding_machine m on l.fmachine_id=m.fid WHERE 1=1 


		AND l.FWeldTime >= #{dto.dtoTime1}


		AND l.FWeldTime <= #{dto.dtoTime2}


AND m.fequipment_no IN (
SELECT m.fequipment_no FROM tb_live_data l 
INNER join tb_welding_machine m on l.fmachine_id=m.fid WHERE 1=1 


		AND l.FWeldTime >= #{dto.dtoTime1}


		AND l.FWeldTime <= #{dto.dtoTime2}


AND l.fstatus='0' 
GROUP BY m.fequipment_no) 
GROUP BY m.fequipment_no
