mapper.select:
SELECT m.fid fid,r.fid rid, wm.fid wid, r.ftype_id ftype_id, fequipment_no,fviceman,d.fvaluename typename,r.fmoney, fstart_time,fend_time,fdesc,finsframework_id insfid FROM tb_welding_maintenance m inner join tb_maintenance_record r on fmaintenance_id = r.fid INNER JOIN tb_dictionary d on r.ftype_id = d.fvalue inner join tb_welding_machine wm on m.fwelding_id = wm.fid INNER JOIN tb_insframework i on i.fid = wm.finsframework_id INNER JOIN tb_insframework ins on ins.fid=i.fparent INNER JOIN tb_insframework insf on insf.fid = ins.fparent where 1=1
and wm.fid = #{wid}
and ${str}
ORDER BY m.fid
