mapper.select:
SELECT g.*,i.fname itemname FROM tb_gather g LEFT join tb_insframework i on i.fid=g.fitemId
where 1=1

	and i.fid=#{parent}


	and ${str}

ORDER BY nlssort(g.fgather_no,'NLS_SORT=SCHINESE_PINYIN_M')

SELECT g.*,i.fname itemname FROM tb_gather g inner join tb_insframework i on i.fid=g.fitemId where g.fid not in (select g.fid from tb_gather g INNER JOIN tb_welding_machine m on m.fid = g.FMACHINE_ID)
and i.fid=#{insfid}
ORDER BY nlssort(g.fgather_no,'NLS_SORT=SCHINESE_PINYIN_M')
