mapper.select:
SELECT m.fid fid,r.fid rid, wm.fid wid, r.ftype_id ftype_id, fequipment_no,fviceman,d.fvaluename typename, fstart_time,fend_time,fdesc FROM tb_welding_maintenance m inner join tb_maintenance_record r on fmaintenance_id = r.fid INNER JOIN tb_dictionary d on r.ftype_id = d.fvalue inner join tb_welding_machine wm on m.fwelding_id = wm.fid LEFT JOIN tb_insframework i ON wm.finsframework_id = i.fid LEFT JOIN tb_insframework ins on ins.fid = i.fparent LEFT JOIN tb_insframework insf on insf.fid = ins.fparent where d.ftypeid=15
and wm.fid = #{wid}
and i.fid=#{parent} or ins.fid=#{parent} or insf.fid=#{parent} or insf.fparent=#{parent}
and ${str}
ORDER BY m.fid

mapper.update:
UPDATE tb_maintenance_record SET ftype_id=#{typeId},fviceman=#{viceman},fdesc=#{desc},

FSTART_TIME = to_date(#{startTime}, 'yyyy-MM-dd HH24:mi:ss'),FEND_TIME = to_date(#{endTime}, 'yyyy-MM-dd HH24:mi:ss')
WHERE fid = #{id}
