mapper.select:
SELECT fsolder_layer id,fweld_bead insid,felectricity fstandardele,fvoltage fstandardvol,
FWeldTime fweldingtime,fstatus fstatus FROM tb_live_data
WHERE fjunction_no = #{fid} and fmachine_id = #{mach} and (fstatus='3' or fstatus='5' or fstatus='7' or fstatus='99')

	and fwelder_no = #{welderid}



		and fweldtime >= to_date(#{dto.dtoTime1}, 'yyyy-mm-dd hh24:mi:ss')


		and fweldtime <= to_date(#{dto.dtoTime2}, 'yyyy-mm-dd hh24:mi:ss')



	and fsolder_layer = #{fsolder_layer}


	and fweld_bead = #{fweld_bead}

SELECT s.fweld_ele+s.fweld_tuny_ele fstandardele,s.fweld_ele-s.fweld_tuny_ele frealele,s.fweld_vol+s.fweld_tuny_vol fstandardvol,s.fweld_vol-s.fweld_tuny_vol frealvol FROM tb_welded_junction j 
LEFT JOIN tb_specification s ON j.fwpslib_id=s.fwpslib_id 
WHERE j.fwelded_junction_no=#{taskNum} AND s.fsolder_layer=#{fsolder_layer} AND s.fweld_bead=#{fweld_bead}
