mapper.select:
SELECT j.*,w.fid fwelder_id,w.fname fwelder_name,l.fwps_lib_name,i.fid iid,i.fname iname,r.foperatetype fstatus FROM tb_welded_junction j LEFT JOIN tb_welder w on j.foperator=w.fid LEFT JOIN tb_wps_library l on j.fwpslib_id=l.fid INNER JOIN tb_insframework i on j.fitemId=i.fid LEFT JOIN tb_taskresult r on r.ftaskid=j.fid WHERE 1=1 and (r.fid IN (SELECT MAX(fid) FROM tb_taskresult GROUP BY ftaskid) OR r.foperatetype IS null)
and ${str}
ORDER BY j.fid

SELECT r.fid,r.ftaskid counts,i.fid iid, i.fname iname,r.foperatetype fdyne,r.fresult fpipeline_no,r.fresultid fupdatecount,u.users_name fmaterial,j.fwelded_junction_no, d.fvaluename froom_no,r.foperatedate fupdatetime,r.frealstarttime fstart_time,r.frealendtime fend_time FROM tb_taskresult r INNER JOIN tb_welded_junction j ON r.ftaskid=j.fid INNER JOIN tb_insframework i on j.fitemId = i.fid LEFT JOIN tb_users u ON u.id=r.foperator left JOIN tb_dictionary d ON r.fresultid=d.fvalue AND d.ftypeid=16 WHERE 1=1 AND r.foperatetype!=3 AND r.fid IN (SELECT MAX(fid) FROM tb_taskresult GROUP BY ftaskid)
and ${str}
ORDER BY r.foperatedate desc

SELECT `FWeldTime` FROM `tb_live_data` WHERE `fmachine_id`=#{machineid} AND `fjunction_no`=#{junid} 

	AND `fwelder_no`=#{welderid}



		and fweldtime >= to_date(#{dto.dtoTime1}, 'yyyy-mm-dd hh24:mi:ss')


		and fweldtime <= to_date(#{dto.dtoTime2}, 'yyyy-mm-dd hh24:mi:ss')


AND `tb_live_data`.`fstatus`='3' ORDER BY `FWeldTime` ASC LIMIT 0,1

SELECT `FWeldTime` FROM `tb_live_data` WHERE `fmachine_id`=#{machineid} AND `fjunction_no`=#{junid} 

	AND `fwelder_no`=#{welderid}



		and fweldtime >= to_date(#{dto.dtoTime1}, 'yyyy-mm-dd hh24:mi:ss')


		and fweldtime <= to_date(#{dto.dtoTime2}, 'yyyy-mm-dd hh24:mi:ss')


AND `tb_live_data`.`fstatus`='3' ORDER BY `FWeldTime` DESC LIMIT 0,1

select COUNT(l.fid) counts,m.fequipment_no,m.fid machid,fwelded_junction_no,fdyne,l.fmax_electricity,l.fmin_electricity,l.fmax_voltage fmax_valtage,l.fmin_voltage fmin_valtage from tb_live_data l INNER join tb_welded_junction j on l.fjunction_id = j.fid INNER JOIN tb_welding_machine m on m.fid=l.fmachine_id where 1=1
and l.fwelder_no=#{welderid}
and l.fjunction_no = #{dto.search}
and (fweldtime between to_date(#{dto.dtoTime1}, 'yyyy-mm-dd hh24:mi:ss')
and to_date(#{dto.dtoTime2}, 'yyyy-mm-dd hh24:mi:ss'))
and (l.fstatus='3' or l.fstatus='5' or l.fstatus='7' or l.fstatus='99') group by m.fequipment_no,fwelded_junction_no

    SELECT * from (
	SELECT * from (
    SELECT fsolder_layer fserial_no,fweld_bead froom_no FROM tb_work WHERE 1=1 

    	and fjunction_no=#{taskno} 



			and fstarttime >=to_date(#{dto.dtoTime1}, 'yyyy-mm-dd hh24:mi:ss') 


			and fendtime <=to_date(#{dto.dtoTime2}, 'yyyy-mm-dd hh24:mi:ss')


    UNION ALL 
    SELECT fsolder_layer fserial_no,fweld_bead froom_no FROM tb_live_data WHERE 1=1 

      and fjunction_no=#{taskno} 


      and FUploadDateTime >= #{time} 

    ) t WHERE fserial_no!=0 group by fserial_no,froom_no
	) te order by froom_no desc

SELECT distinct j.fid,fwelded_junction_no,ftask_no,fitemId iid,i.fname iname,j.flag,l.fwps_lib_name,l.fwps_lib_version,j.fstatus,DECODE(t.FTASKID,NULL,t.FOPERATETYPE,0) roomNo, DECODE(j.fstatus, 2, j.fback ,NULL) fback,DECODE(SIGN(COUNT(DISTINCT(p.fproduct_number))-COUNT(DISTINCT(t.ftaskid))), 1, 1 ,0) fdyne,l.FPRODUCT_DRAWING_NO,l.FPRODUCT_NAME, case when COUNT(distinct t.FCARD_ID)=0 THEN NULL when COUNT(distinct s.fid)-COUNT(distinct t.FSTEP_ID)=0 and t.FOPERATETYPE=1 THEN 1 ELSE 0 END fmaterial FROM tb_welded_junction j LEFT JOIN tb_insframework i ON j.fitemId=i.fid LEFT JOIN tb_insframework ins on ins.fid = i.fparent LEFT JOIN tb_insframework insf on insf.fid = ins.fparent LEFT JOIN tb_product_number p ON j.fid=p.fcard_id LEFT JOIN tb_wps_library l ON p.fwps_lib_id=l.fid LEFT JOIN TB_EMPLOYEE e on e.FWPS_LIB_ID=l.fid LEFT JOIN TB_STEP s on e.fid=s.FEMPLOYEE_ID LEFT JOIN tb_taskresult t ON j.fid=t.fcard_id
WHERE ${search}
GROUP BY j.fid,j.fwelded_junction_no,ftask_no,fitemId,i.fname,j.flag,l.fwps_lib_name,l.fwps_lib_version,j.fstatus,t.FTASKID,t.FOPERATETYPE,DECODE(j.fstatus, 2, j.fback ,NULL),l.FPRODUCT_DRAWING_NO,l.FPRODUCT_NAME ORDER BY nlssort(j.fwelded_junction_no,'NLS_SORT=SCHINESE_PINYIN_M')

SELECT DISTINCT p.fid,p.FPRODUCT_NUMBER,p.FPREFIX_NUMBER,p.FSUFFIX_NUMBER,p.FCARD_ID,p.FWPS_LIB_ID,l.fwps_lib_name,l.fwps_lib_version, t.fmachineid machid,l.fid fwpslib_id, l.fproduct_drawing_no,l.fproduct_name,l.fproduct_version,l.flag, case when COUNT(distinct t.FTASKID)=0 THEN NULL when COUNT(distinct s.fid)-COUNT(distinct t.FSTEP_ID)=0 and t.FOPERATETYPE=1 THEN 1 ELSE 0 END FSTATUS FROM tb_product_number p LEFT JOIN tb_wps_library l ON p.fwps_lib_id=l.fid LEFT JOIN TB_EMPLOYEE e on e.FWPS_LIB_ID=l.fid LEFT JOIN TB_STEP s on e.fid=s.FEMPLOYEE_ID LEFT JOIN tb_taskresult t ON p.fid=t.ftaskid WHERE 1=1
${search}
GROUP BY p.fid,p.FPRODUCT_NUMBER,p.FPREFIX_NUMBER,p.FSUFFIX_NUMBER,p.FCARD_ID,p.FWPS_LIB_ID,l.fwps_lib_name,l.fwps_lib_version, t.fmachineid,l.fid, l.fproduct_drawing_no,l.fproduct_name,l.fproduct_version,l.flag,t.FOPERATETYPE ORDER BY p.FID ASC

mapper.update:
update tb_product_number set 
fwps_lib_id=#{fwpslib_id}
where fcard_id=#{id} AND fid NOT IN (SELECT DISTINCT ftaskid FROM tb_taskresult WHERE fcard_id=#{id})

mapper.select:
SELECT wj.fwelded_junction_no,wl.fwps_lib_name,wl.FPRODUCT_DRAWING_NO,wl.FPRODUCT_NAME,wl.FPRODUCT_VERSION,wl.fwps_lib_version,pn.* FROM tb_product_wps pw LEFT JOIN tb_welded_junction wj ON pw.fcard_id=wj.fid LEFT JOIN tb_product_number pn ON pw.fproduct_id=pn.fid LEFT JOIN tb_wps_library wl ON pw.fwps_id=wl.fid
WHERE ${search}
order by pw.fid DESC
